import React, { Component ,useState} from "react";
import LayoutContentWrapper from "../../../components/utility/layoutWrapper.js";
import LayoutContent from "../../../components/utility/layoutContent";
import Form from '../../../components/uielements/form';
import IntlMessages from '../../../components/utility/intlMessages';
import Input from '../../../components/uielements/input';

const FormItem = Form.Item;
const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 5 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 12 }
  }
};




const Setting = () => {

  const FromT =() =>(
    <FormItem
    {...formItemLayout}
    label={<IntlMessages id="เลขประจำตัวผู้เสียภาษี" />}
  >
    <Input placeholder="Text1234567" name = "id" value={form.id} onChange = {updateField}  />
  </FormItem>
  )

  const [form,setValues] = useState({
    id:'',
    name:'',
    subid:'',
    location:'',
    no:'',
    road:'',
    dis:'',
    tel:''
  })
  
  const updateField = e => {
 
    setValues({
        ...form,
        [e.target.name]:e.target.value
      })
     
    }


    return (
      <LayoutContentWrapper style={{ height: "100vh" }}>
        <LayoutContent>

          <FromT />

          <FormItem
              {...formItemLayout}
              label={<IntlMessages id="เลขประจำตัวผู้เสียภาษี" />}
            >
              <Input placeholder="Text1234567" name = "id" value={form.id} onChange = {updateField}  />
            </FormItem>

            <FormItem
              {...formItemLayout}
              label={<IntlMessages id="ชื่อร้านค้า" />}
            >
              <Input placeholder="Alethia" name = "name" value={form.name} onChange = {updateField}  />
            </FormItem>

            <FormItem
              {...formItemLayout}
              label={<IntlMessages id="เลขที่สาขา" />}
            >
              <Input placeholder="0" name = "subid" value={form.subid} onChange = {updateField}  />
            </FormItem>

            <FormItem
              {...formItemLayout}
              label={<IntlMessages id="ที่ตั้งร้านค้า" />}
            >
              <Input placeholder="夜の国" name = "location" value={form.location} onChange = {updateField}  />
            </FormItem>

   

        </LayoutContent>
      </LayoutContentWrapper>
    );
  
}
export default Setting
