import React, { Component ,useState, useEffect} from "react";
import LayoutContentWrapper from "../../../components/utility/layoutWrapper.js";
import LayoutContent from "../../../components/utility/layoutContent";
import Form from '../../../components/uielements/form';
import IntlMessages from '../../../components/utility/intlMessages';
import Input ,{InputGroup}from '../../../components/uielements/input';
import Button, { ButtonGroup } from '../../../components/uielements/button';
import { Col, Row, Icon } from 'antd';
import { RadioGroup, RadioButton } from '../../../components/uielements/radio';

import "./style_0.css"
import "../style_all.css"
const FormItem = Form.Item;
const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 7 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 12 }
  }
};
const formConfig = {
  borderColor:"#654018"
}

const range = e => Array(e).fill(0).map((x,i) =>i)

const Setting = () => {

const cat5 = () =>{
  let t = form.cat
  t= t.slice(0,5)
  setValues({
    ...form,
    cat:t
  })
}

const cat10 =() =>{
  let t = form.cat
  console.log(t)
  if(t.length==5)
    t = [...t,'','','','','']
  setValues({
    ...form,
    cat:t
  })
}

  const fecthData = () =>{
    console.log("A")
    console.log(range(5))
  }

  const proto = ['ร้านเสื้อผ้าเครื่องแต่งกาย','ร้านเครื่องสำอางค์','ร้านเครื่องประดับ','ร้านเครื่องใช้ไฟฟ้า','ร้านคอมพิวเตอร์']
  const addField = (c) =>{
    console.log(c)
    let t = form.cat
    if(c=='ร้านเสื้อผ้าเครื่องแต่งกาย'){

      t[0] = 'เสื้อ'
      t[1] = 'กางเกง'
      t[2] = 'รองเท้าผ้าใบ'
      t[3] = 'รองเท้าหนัง'
      t[4] = 'กระเป๋าหนัง'
    }
    if(c=='ร้านเครื่องสำอางค์'){

    }
      setValues({
        ...form,
        cat: t
      })

  }
  const [form,setValues] = useState({
    id:'',
    name:'',
    subid:'',
    no:'',
    road:'',
    dis:'',
    pro:'',
    tel:'',
    cat :['','','','','']
  })



  const updateField = e => {

    setValues({
        ...form,
        [e.target.name]:e.target.value
      })

    }

    const updateArray =(e,f)=> {
      let t = form.cat
      t[f] = e.target.value 

      setValues({
          ...form,
          [form.cat]:t
        })

      }



    return (
      <LayoutContentWrapper>
        <LayoutContent>
        <div style  = {{display:'flex',justifyContent:"center",width:'100%'}}>
      <div style = {{textAlign:'center',backgroundColor:"#654018" , width:"200px",marginBottom:"20px"}}>
      <h2 style = {{color:"white"}}>Setting up</h2>
